# be careful using the recursion!
def _isSubsequence(s, t):
    if s and t:
        if len(s) == 1 or len(t) == 1:
            return s in t
        if s[-1] == t[-1]:
            return isSubsequence(s[:-1], t[:-1])
        else:
            return isSubsequence(s, t[:-1])
    else:
        return s in t


def isSubsequence(s, t):
    q = list(s)
    for c in t:
        if not q:
            return True
        if c == q[0]:
            q.pop(0)
    return not q


if __name__ == "__main__":
    print
    isSubsequence("abc", "ahbgdc")
    print
    isSubsequence("axc", "ahbgdc")
    print
    isSubsequence("", "ahbgdc")
    print
    isSubsequence("abc", "")
    print
    isSubsequence("leetcode",
                  "")
