import osCHECK_FILE = (    'java',    'jsp',    'html',    'htm',    'xml',    'json',    'txt',)def get_file(path, files):    if os.path.isdir(path):        _files = os.listdir(path)        for f in _files:            _path = path + '/' + f            if os.path.isdir(_path):                get_file(_path, files)            else:                files.append(_path)    else:        files.append(path)def is_chinese(c):    return u'\u4e00' <= c <= u'\u9fff'def is_check_file(f):    return f.split('.')[-1] in CHECK_FILEdef check(path):    files = []    get_file(path, files)    for f in files:        if is_check_file(f):            with open(f) as fp:                content = fp.read()                for c in content.decode('utf-8'):                    if is_chinese(c):                        print                        f                        break